 const Person = ({name, age, click, children, changed, index}) => {
    return(
        <div className="Person">
            <p onClick={click}>Hello my name is {name}. I am {age} years old</p>
            <p>{children}</p>
            <input type="text" onChange={changed} value={name}/>
        </div>
    );
}
export default Person;
